import React, { Component, Fragment } from 'react';
import { API_ROOT, HEADERS } from '../constants';
import { Redirect } from 'react-router-dom';

class GamesPage extends Component {
  state = {
    availableGames: [],
    gameName: '',
    redirect: false,
    currentGameId: '',
    currentGameMode: ''
  }

  componentDidMount() {
    // initial load of available games
    fetch(`${API_ROOT}/game`)
      .then(response => response.json())
      .then(json => this.setState({
        availableGames: json}))
  }

  // add websocket listener to pull in new created games

  handleAddGames = (ev) =>{
    ev.preventDefault()
    const playerId = JSON.parse(localStorage.getItem('playerData')).playerId
    const gameId = ev.target.id

    fetch(`${API_ROOT}/game/${gameId}`, {
      method: 'PATCH',
      headers: HEADERS,
      body: JSON.stringify({playerId})
    })
      .then(
        this.setState({
          redirect:true,
          currentGameId: gameId,
          currentGameMode: 'draw'
      }))

  }

  handleJoiningGame = (ev) => {
    // post player joining game, redirect to game page
    const playerId = JSON.parse(localStorage.getItem('playerData')).playerId
    const gameId = ev.target.id

    fetch(`${API_ROOT}/game/${gameId}`, {
      method: 'PATCH',
      headers: HEADERS,
      body: JSON.stringify({playerId})
    })
      .then(
        this.setState({
          redirect:true,
          currentGameId: gameId,
          currentGameMode: 'guess'
      }))
  }

  renderRedirect = () => {
    if (this.state.redirect) {
      return <Redirect to={`/${this.state.currentGameId}/${this.state.currentGameMode}`}/>
    }
  }

  render() {
    return (
      <Fragment>
        {this.renderRedirect()}

        Game Name:
        <form onSubmit={this.handleAddGames} >
          <input type='text' placeholder='New game name.' name='createGame' required/>
          <input type='submit' value='create game' />
        </form>
        <h3>Available Games:</h3>
        <div className='game-card-container'>
          {this.state.availableGames.map(game => {
            return (
              <div
                key={game.id}
                value={game}
                className='game-card'
              >
                <h4>Game: {game.id}</h4>
                <h4>Name: {game.name}</h4>
                <p>Drawer_Id: {game.drawer_id}</p>
                <button
                  id={game.id}
                  name={game.id}
                  onClick={this.handleJoiningGame}
                  className='button'
                >
                  Play as a guess role
                </button>

                <button
                  id={game.id}
                  name={game.id}
                  onClick={this.handleJoiningGame}
                  className='button'
                >
                  Play as a draw role
                </button>
                </div>
              )
            })
            }
          </div>
      </Fragment>
    )
  }
}
export default GamesPage;
