import React, { Component, Fragment } from 'react';
import { API_ROOT, HEADERS } from '../constants';
import { ActionCableConsumer } from 'react-actioncable-provider';

class GuessList extends Component {
  constructor(props) {
     super(props)
     this.state = {
      guessList: [],
      rejectedGuesses: [],
      rejectValue: ''
     }
   }

  componentDidMount() {
     // fetch(API_ROOT+`/game/${this.props.gameId}`)
     // .then(resp => resp.json())
     // .then(json => {
     //   if (json) {
     //   this.setState({
     //     guessList: json,
     //     rejectedGuesses: json
     //   })}
     // })
     setInterval(this.updateMessage, 4000)
   }

  handleReject = (ev) => {
    console.log('checkBuytton',ev.target.value)
    let tempArray = this.state.rejectedGuesses.slice()
    tempArray.push(ev.target.value)
    this.setState({
      rejectedGuesses: tempArray
    })
    // fetch(API_ROOT+`/game/${this.props.gameId}`)
    // .then(resp => resp.json())
    // .then(json =>{
    //   console.log('updateMessage', json)
    //   let rejectList = this.state.rejectedGuesses.slice()
    //   rejectList.push(json[json.length-1])
    //   this.setState({
    //     rejectedGuesses: rejectList
    //   })
    // })


   }

updateMessage = () =>{
  fetch(API_ROOT+`/game/${this.props.gameId}`)
  .then(resp => resp.json())
  .then(json =>{
    // console.log('updateMessage', json)
    this.setState({
      guessList: json[json.length-1],
      rejectValue: json[json.length-1]
    })

    if(this.handleReject){
      let tempArray = this.state.rejectedGuesses.slice()
      tempArray.push(json[json.length-1])
      this.setState({
        rejectedGuesses: tempArray
      })
    }
  })

  // console.log(this.state.buttonValue)
  // alert('We have a Winner, Thanks for playing.')
  // setTimeout(window.location = 'http://localhost:3001/games', 3000)
}

  render() {
    if (this.props.isDrawing) {
      this.acc || (this.acc = null)
      return (
        <Fragment>
          {this.acc}
          <h4>Guess List</h4>
          <ul>
                <li>{this.state.rejectValue}
                  <button

                    name='Accept'
                    onClick={this.handleClick}
                    value={null}
                  >
                    Accept
                  </button>
                  <button

                    name='Reject'
                    onClick={this.handleReject}
                    value={this.state.rejectValue}
                  >
                    Reject
                  </button>
                </li>

          </ul>
        </Fragment>
      )
    } else {
      this.acc || (this.acc = null)
      return (
        <Fragment>
          {this.acc}
          <h4>Rejected Guess List</h4>
          <ul>
            {this.state.rejectedGuesses.map((guess, idx) => {
              return <li key={idx} style={{textDecoration: 'line-through'}}>{guess}</li>
              })}
          </ul>
        </Fragment>
      )
    }
  }
}

export default GuessList
