import React, { Component, Fragment } from 'react';
import { API_ROOT, HEADERS } from '../constants';
import { ActionCableConsumer } from 'react-actioncable-provider';

class GuessList extends Component {
  constructor(props) {
     super(props)
     this.state = {
      guessList: [],
      rejectedGuesses: [],
      guessValue: ''
     }
   }

  componentDidMount() {
    const movement = {
      isClear: "true"
    }
    fetch(API_ROOT + `/game/${this.props.gameId}`, {
      method: 'PATCH',
      headers: HEADERS,
      body: JSON.stringify(movement)
    })

    setInterval(this.updateList, 4000)
   }

  handleClick = (ev) => {
     // ev.persist()
     const guessIdx = ev.target.id
     const guessAction = ev.target.name
     const guessText = ev.target.value
     const type = 'guessStatus'
     const isReject = true

     fetch(API_ROOT+`/game/${this.props.gameId}`,{
       method: 'PATCH',
       headers: HEADERS,
       body: JSON.stringify({guessIdx, guessAction, guessText, type, isReject})
     })
      .then(response => response.json())
      .then(json => {
        if (json.message == 'Wrong!') {
          this.setState(prevState => ({
            rejectedGuesses: [...prevState.rejectedGuesses, json.guessText]
          })
        )} else if (json.message == 'Correct!') {
          this.props.endGame('rightAnswer')
          alert('We have a Winner, Thanks for playing.')
          setTimeout(window.location = 'http://localhost:3001/games', 3000)
        }
      })

   }

   updateList = ()=>{
     fetch(API_ROOT+`/game/${this.props.gameId}`)
     .then(resp => resp.json())
     .then(json =>{
       this.setState({
         guessList: json.guesses,
         rejectedGuesses: json.rejectList,
         guessValue: json.guessInput
       })
     })
   }

   handleReject = () =>{
     const sendReject = {
       isReject: "true"
     }
     fetch(API_ROOT + `/game/${this.props.gameId}`, {
       method: 'PATCH',
       headers: HEADERS,
       body: JSON.stringify(sendReject)
     })

   }


  render() {
    if (this.props.isDrawing) {
      this.acc || (this.acc = null)
      return (
        <Fragment>
          {this.acc}
          <h4>Guess List</h4>
          <ul>
            {this.state.guessList.map((guess, idx) => {
              return (
                <li key={idx}>{guess}
                  <button
                    id={idx}
                    name='Accept'
                    onClick={this.handleClick}
                    value={guess}
                  >
                    Accept
                  </button>
                  <button
                    id={idx}
                    name='Reject'
                    onClick={this.handleClick}
                    value={guess}
                  >
                    Reject
                  </button>
                </li>
              )})
            }
          </ul>
        </Fragment>
      )
    } else {
      this.acc || (this.acc = null)
      return (
        <Fragment>
          {this.acc}
          <h4>Rejected Guess List</h4>
          <ul>
            {this.state.rejectedGuesses.map((guess, idx) => {
              return <li key={idx} style={{textDecoration: 'line-through'}}>{guess}</li>
              })}
          </ul>
        </Fragment>
      )
    }
  }
}

export default GuessList
